mapper.select:
select count(1) from shopcontext s
LEFT JOIN userinformation u on s.uid=u.id
LEFT JOIN shopinformation t ON s.sid=t.id
where 1=1

	and u.username=#{params.username}


	and t.name=#{params.shopname}

select s.id,s.modified,s.display,s.context,u.username,t.name from shopcontext s
LEFT JOIN userinformation u on s.uid=u.id
LEFT JOIN shopinformation t ON s.sid=t.id
where  1=1

and u.username=#{params.username}


  and t.name=#{params.shopname}

limit #{offset}, #{limit}
